import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import { useEffect } from "react"
import * as React from "react"
// import FormatBoldIcon from '@mui/icons-material/FormatBold';
// import FormatItalicIcon from '@mui/icons-material/FormatItalic';
// import FormatUnderlinedIcon from '@mui/icons-material/FormatUnderlined';
// import FormatColorFillIcon from '@mui/icons-material/FormatColorFill';
// import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ToggleButton from "@mui/material/ToggleButton"
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup"

function ToggleButtonSet() {
  useEffect(() => Streamlit.setFrameHeight())
  const [formats, setFormats] = React.useState(() => ["bold", "italic"])

  const handleFormat = (
    event: React.MouseEvent<HTMLElement>,
    newFormats: string[]
  ) => {
    setFormats(newFormats)
  }

  return (
    <ToggleButtonGroup
      color="primary"
      value={formats}
      onChange={handleFormat}
      aria-label="text formatting"
    >
      <ToggleButton value="Hello" aria-label="bold">
        hello
      </ToggleButton>
      <ToggleButton value="World" aria-label="italic">
        world
      </ToggleButton>
      <ToggleButton value="!" aria-label="underlined">
        ‚ùó
      </ToggleButton>
    </ToggleButtonGroup>
  )
}

export default withStreamlitConnection(ToggleButtonSet)
