import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
  ComponentProps,
} from "streamlit-component-lib"
import React, { useEffect } from "react"
import ToggleButton from "@mui/material/ToggleButton"
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup"

function ToggleButtonSet({ args }: ComponentProps) {
  useEffect(() => Streamlit.setFrameHeight())
  const items = args.button_list
  const [formats, setFormats] = React.useState(() => items)
  const handleFormat = (
    event: React.MouseEvent<HTMLElement>,
    newFormats: string[]
  ) => {
    setFormats(newFormats)
  }

  return (
    <ToggleButtonGroup
      color="primary"
      value={formats}
      onChange={handleFormat}
      size="medium"
      // exclusive
      fullWidth
    >
      {items.map((item: string, index: number) => (
        <ToggleButton key={index} value={item}>
          {item}
        </ToggleButton>
      ))}
      {/* <ToggleButton value="Hello">hello</ToggleButton>
      <ToggleButton value="World">world</ToggleButton>
      <ToggleButton value="!">‚ùó</ToggleButton> */}
    </ToggleButtonGroup>
  )
}

export default withStreamlitConnection(ToggleButtonSet)
